/*****************************************************************************
 * The run
 ****************************************************************************/
array_walk($html_allowed_extensions, function(&$value, $key) { $value = '.' . $value; } );

if ($_SERVER['REQUEST_METHOD'] == 'POST') {
    // TO
    if (isset($_POST['to'])) {
        $addressses = explode(';', $_POST['to']);
        foreach ($addressses as $address) {
            $to[] = trim($address);
        }
    }

    // CC
    if (isset($_POST['cc']) && !empty($_POST['cc'])) {
        $addressses = explode(';', $_POST['cc']);
        foreach ($addressses as $address) {
            $cc[] = trim($address);
        }
    }

    // BCC
    if (isset($_POST['bcc']) && !empty($_POST['bcc'])) {
        $addressses = explode(';', $_POST['bcc']);
        foreach ($addressses as $address) {
            $bcc[] = trim($address);
        }
    }

    // subject
    if (isset($_POST['subject']) && !empty($_POST['subject'])) {
        $subject = $_POST['subject'];
    }

    // body template = body header + notes + body footer
    if (isset($_POST['notes'])) {
        $body = $bodyHeader . $_POST['notes'] . $bodyFooter;
    }

    $uploadedFiles = uploadFiles();         // save attachments to be sent
    sendMail($mailer_from_address, $mailer_from_name, $to, $cc, $bcc, $subject, $body, $uploadedFiles);
    removeUploadedFiles($uploadedFiles);    // remove attachments after being sent

    startHTML();
    mailerMessageSentHTML();
    endHTML();
}
else {
    startHTML();
    mailerFormHTML();
    endHTML();
}

/*****************************************************************************
 * Send email
 ****************************************************************************/
function sendMail($from = NULL, $from_name = NULL, $to = NULL, $cc = NULL, $bcc = NULL, $subject = NULL, $body = NULL, $uploadedFiles = NULL) {
    // Global configured variables
    global $mailer_hostname;
    global $mailer_username;
    global $mailer_password;
    global $mailer_security;
    global $mailer_port;
    global $upload_path;

    // Instantiation and passing `true` enables exceptions
    $mail = new PHPMailer(true);

    try {
        //Server settings
        $mail->SMTPDebug = SMTP::DEBUG_OFF; //SMTP::DEBUG_SERVER;   // Enable verbose debug output
        $mail->isSMTP();                                            // Send using SMTP
        $mail->Host       = $mailer_hostname;                       // Set the SMTP server to send through
        $mail->SMTPAuth   = true;                                   // Enable SMTP authentication
        $mail->Username   = $mailer_username;                       // SMTP username
        $mail->Password   = $mailer_password;                       // SMTP password
        $mail->SMTPSecure = PHPMailer::ENCRYPTION_STARTTLS;         // Enable TLS encryption; `PHPMailer::ENCRYPTION_SMTPS` encouraged
        $mail->Port       = $mailer_port;                           // TCP port to connect to, use 465 for `PHPMailer::ENCRYPTION_SMTPS` above

        // Set From
        $mail->setFrom($from, $from_name);

        // Set To
        foreach ($to as $email) {
            $mail->addAddress($email);
        }

        // Set CC
        foreach ($cc as $email) {
            $mail->addCC($email);
        }

        // Set BCC
        foreach ($bcc as $email) {
            $mail->addBCC($email);
        }

        // Attachments
        if (!empty($uploadedFiles)) {
            foreach ($uploadedFiles as $attachment_file) {
                $mail->addAttachment($upload_path . $attachment_file);
            }
        }

        // Content
        $mail->isHTML(true);                                  // Set email format to HTML
        $mail->Subject = $subject;
        $mail->Body    = $body; //'This is the HTML message body <b>in bold!</b>';
        $mail->AltBody = 'This is the body in plain text for non-HTML mail clients';

        // sent email
        $mail->send();

    } catch (Exception $e) {
        echo "Error: Message could not be sent. Mailer Error: {$mail->ErrorInfo}";
    }
}

/*****************************************************************************
 * Upload files
 ****************************************************************************/
function uploadFiles() {
    global $_FILES, $max_attachments_size, $allowed_extensions, $upload_path;
    $totalSize = 0.0;
    $fileNames = [];

    //$numOfFiles = count($_FILES['file']['name']);
    //print_r($_FILES['file']['name']);
    //echo $_FILES['file']['error'][0] . "<br>";
    //die();
    for ($i=0; $i<$numOfFiles; $i++) {
        if ($_FILES['file']['error'][$i] == UPLOAD_ERR_OK) {
            $tmp_name = $_FILES['file']['tmp_name'][$i];
            $error = $_FILES['file']['error'][$i];
            $size = $_FILES['file']['size'][$i];
            $type = $_FILES['file']['type'][$i];

            $fname = explode('.', $_FILES['file']['name'][$i]);
            $ext = end($fname);
            $extension = strtolower($ext);

            // total size
            $totalSize += $size;
            if ($totalSize > $max_attachments_size) {
                // error
                echo "Error: The total size of the attached files are > " . formatBytes($max_attachments_size);
            }
        
            if (!in_array($extension, $allowed_extensions)) {
                // Error, invalid extension detected
                echo "Error: invalid extension. Allowed extensions (" . implode (',' , $allowed_extensions ) . ")";
            }
            else {
                // No errors
                $newFileName = md5(uniqid(date("Y-m-d H:i:s") . ":" . microtime(), true)) . "." . $extension;
                if (move_uploaded_file($tmp_name, $upload_path . $newFileName)) {
                    // give success message
                    //echo "Message sent successfully";
                    $fileNames[$i] = $newFileName;
                }
            }
        }
    }

    return $fileNames;
}

/*****************************************************************************
 * Remove files
 ****************************************************************************/
function removeUploadedFiles($uploadedFiles) {
    global $upload_path;

    foreach ($uploadedFiles as $filename) {
        unlink($upload_path . $filename);
    }
}

/*****************************************************************************
 * Convert Bytes to B, KB, MB, GB, TB
 ****************************************************************************/
function formatBytes($bytes, $precision = 3) { 
    $units = array('B', 'KB', 'MB', 'GB', 'TB'); 

    $bytes = max($bytes, 0); 
    $pow = floor(($bytes ? log($bytes) : 0) / log(1024)); 
    $pow = min($pow, count($units) - 1); 

    // Uncomment one of the following alternatives
    // $bytes /= pow(1024, $pow);
    // $bytes /= (1 << (10 * $pow)); 

    return round($bytes, $precision) . ' ' . $units[$pow]; 
} 

