/*****************************************************************************
 * FileUploadZone JS
 ****************************************************************************/
function includeFileUploadZoneJS() {
    global $max_attachments_size;
    ?>
    <script>
        function formatBytes(bytes, decimals = 2) {
            if (bytes === 0) return '0 Bytes';

            const k = 1024;
            const dm = decimals < 0 ? 0 : decimals;
            const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB', 'ZB', 'YB'];

            const i = Math.floor(Math.log(bytes) / Math.log(k));

            return parseFloat((bytes / Math.pow(k, i)).toFixed(dm)) + ' ' + sizes[i];
        }

        $(function(){
            var totalSize = 0.0;

            $('#file').on('change', function(e){
                $('#upload-file-info').html(""); //clear the info before appending
                var files = $(this).prop('files');
                
                var fileInfoHeader = '<div class="row" style="background-color: #68a5ff;"><div class="col-md-8 text-white fw-bold">File</div><div class="col-md-2 text-white fw-bold">Size</div><div class="col-md-2 text-white fw-bold">Type</div></div>';
                $('#upload-file-info').append(fileInfoHeader);

                for (var i = 0; i < files.length; i++) {
                    var bgColor = '';
                    if (i % 2 == 1) {
                        bgColor = 'style="background-color: #dceaff"';
                    }

                    totalSize += files[i].size;
                    var fileinfo = '<div class="row" '+bgColor+'><div class="col-md-8">'+files[i].name+'</div><div class="col-md-2">'+formatBytes(files[i].size,3)+'</div><div class="col-md-2">'+files[i].type+'</div></div>';
                    $('#upload-file-info').append(fileinfo);
                }

                var textColor = '';
                if (totalSize > <?php echo $max_attachments_size; ?>) {
                    textColor = 'text-danger';
                }
                var fileInfoFooter = '<div class="row"><div class="col-md-8 fw-bold">Total Size</div><div class="col-md-2 fw-bold '+textColor+'">'+formatBytes(totalSize,3)+'</div><div class="col-md-2"></div></div>';
                $('#upload-file-info').append(fileInfoFooter);

                if (totalSize > <?php echo $max_attachments_size; ?>) {
                    var fileInfoFooter = '<div class="row"><div class="col-md-12 text-danger fw-bold">The total size of the attached files must be <= '+formatBytes(<?php echo $max_attachments_size; ?>,3)+'</div></div>';
                    $('#upload-file-info').append(fileInfoFooter);
                }
            });

            $("#submit").on('click', function(e) {
                if (totalSize > <?php echo $max_attachments_size; ?>) {
                    e.preventDefault();
                    alert('The total size of the attached files must be <= '+formatBytes(<?php echo $max_attachments_size; ?>,3));
                }
            });
        });
    </script>
    <?php
}
?>
